Client.GetEntityCoords=function(a,b)local c=type(b)if c=="vector3"or c=="vector4"then return vector4(b.x,b.y,b.z,b.w or 0)end;if not DoesEntityExist(b)then return vector4(0,0,0,0)end;local d=GetEntityCoords(b)return vector4(d.x,d.y,d.z,GetEntityHeading(b))end;Client.GetOffset=function(a,d,e)local f=Client.GetEntityCoords(a,d)local g=f.w or 0;local h=math.rad(g)local i=e.x*math.cos(h)-e.y*math.sin(h)local j=e.x*math.sin(h)+e.y*math.cos(h)local k=f.x+i;local l=f.y+j;local m=f.z+e.z;local n=f.w;return vector4(k,l,m,n)end